export default {
  props: {
    tipo: {
      type: String,
      required: true
    },
    rotulo: {
      type: String,
      required: true
    },
    confirmacao: Boolean,
    estilo: String
  },
  methods: {
    disparaAcao: function disparaAcao() {
      if (this.confirmacao) {
        if (confirm("Confirma operacao?")) {
          this.$emit("botaoAtivado");
        }

        return;
      }

      this.$emit("botaoAtivado");
    }
  },
  computed: {
    estiloDoBotao: function estiloDoBotao() {
      var estiloBotao;

      if (this.estilo == 'padrao' || !this.estilo) {
        estiloBotao = "botao botao-padrao";
      } else if (this.estilo == 'perigo') {
        estiloBotao = "botao botao-perigo";
      }

      return estiloBotao;
    }
  }
};
